mapper.select:

SELECT banner_id, name, banner_placement, banner_description, banner_clicks, banner_views,
		banner_url, banner_weight, banner_active, banner_comment, banner_type, banner_width, banner_height 
FROM jforum_banner 	WHERE banner_id = #{bannerId}

mapper.update:
UPDATE jforum_banner
banner_name = #{name}, banner_placement = #{placement}, banner_description = #{description}, banner_clicks = #{clicks}, banner_views = #{views}, banner_url = #{url}, banner_weight = #{width}, banner_comment = #{comment}, banner_type = #{type}, banner_width = #{weight}, banner_height = #{height}, banner_active = #{active,typeHandler=booleanToIntHandler}
WHERE banner_id = #{bannerId}
